import React, { useState } from 'react';
import { fetchLoginPage } from './services';
import errorMessages from './errorMessages';
import loadingIcon from './spinner.svg';

const LoginPage = ({ loggedIn }) => {
  const [error, setErrorMessage] = useState('');
  const [username, setUser] = useState('');
  const [isLoading, setLoading] = useState(false);


  const performLogin = () => {
    if (!username) {
      setErrorMessage(errorMessages.MANDATORY_USERNAME);
      return;
    }
    setErrorMessage('');
    setLoading(true);
    fetchLoginPage(username)
      .then(userInfo => {
        loggedIn(userInfo.username);
      })
      .catch(err => {
        setErrorMessage(errorMessages[err.code || 'DEFAULT']);
        setLoading(false);
        setUser('');
      });
  };

  const onChange = e => setUser(e.target.value);

  return (
    <div className="login">
      <p className="error">{error}</p>
      <input onChange={onChange} value={username} />
      {isLoading ? (
        <img alt="loadingIcon" src={loadingIcon} />
      ) : (
        <button onClick={performLogin}>Login</button>
      )}
    </div>
  );
};

export default LoginPage;
