import React, { useState, useEffect, useCallback } from 'react';
import { fetchLoginStatus, fetchUsersData, fetchMessageData } from './services';
import Message from './Message';
import Navigation from './Navigation';
import useInterval from './Interval';
import NewMessage from './NewMessage';
import errorMessages from './errorMessages';
import Users from './Users';
import Login from './Login';
import './app.css';

const App = () => {
  const [userState, setLoggedInState] = useState({ isLoggedIn: false });
  const [userListState, setUserList] = useState([]);
  const [messageState, setMessage] = useState([]);
  const [errorState, setErrorMessage] = useState('');

  const getChats = useCallback(() => {
    getUsers();
    getMessages();
  }, []);

  useEffect(() => {
    fetchLoginStatus()
      .then(userInfo => {
        setLoggedInState({
          isLoggedIn: true,
          username: userInfo.username
        });
        if (userState.isLoggedIn) {
          getChats();
        }
        setErrorMessage('');
      })
      .catch(err => {
        setErrorMessage(errorMessages[err.code || 'DEFAULT']);
      });
  }, [getChats, userState.isLoggedIn]);

  const login = username => {
    setLoggedInState({
      isLoggedIn: true,
      username
    });
    getChats();
  };

  const logout = () => {
    setLoggedInState({
      isLoggedIn: false
    });
  };

  const getUsers = () => {
    fetchUsersData()
      .then(usersList => {
        setUserList(Object.values(usersList));
      })
      .catch(err => {
        setErrorMessage(errorMessages[err.code || 'DEFAULT']);
      });
  };

  const getMessages = () => {
    fetchMessageData()
      .then(messageList => {
        setMessage(messageList);
      })
      .catch(err => {
        setErrorMessage(errorMessages[err.code || 'DEFAULT']);
      });
  };

  useInterval(() => {
    if (userState.isLoggedIn) {
      getChats();
    }
  }, 3000);

  let content;

  const checkError = errCode => {
    if (errCode === 'MANDATORY_LOGIN' || errCode === 'UNAUTHORIZED_ACCESS') {
      logout();
    }
  };

  if (userState.isLoggedIn) {
    content = (
      <React.Fragment>
        <NewMessage onAddMessage={getMessages} onError={checkError} />
        <div className="display-container">
          <Users userList={userListState} />
          <Message messageList={messageState} />
        </div>
      </React.Fragment>
    );
  } else {
    content = <Login loggedIn={login} />;
  }

  return (
    <div className="app">
      <Navigation user={userState} onLogout={logout} onError={checkError} />
      <p>{errorState}</p>
      {content}
    </div>
  );
};

export default App;
