import React, { useState } from 'react';
import { fetchLogoutPage } from './services';
import errorMessages from './errorMessages';

const Navigation = ({ user, onLogout, onError }) => {
  const [error, setError] = useState('');

  const logout = () => {
    fetchLogoutPage()
      .then(() => {
        onLogout();
      })
      .catch(err => {
        onError(err.code);
        setError(errorMessages[err.code || 'DEFAULT']);
      });
  };
  return (
    <div>
      <ul className="nav">
        {user.username && <li> {user.username}</li>}
        <p className="header">Chat Application</p>
        {user.isLoggedIn && (
          <li className="logout action" onClick={logout}>
            Logout
          </li>
        )}
      </ul>
      <p>{error}</p>
    </div>
  );
};

export default Navigation;
